//moduel returns the last directory name of a string in a format of path-directory
//example : ../Siri-Law-Br/source/texts/cdc_en/

var getLastDirName = function(str){
  var indexLast1 = str.lastIndexOf('/');
  var dirName = str.substring(0, indexLast1-1);

  var indexLast = dirName.lastIndexOf('/');
  dirName = str.substring(++indexLast, indexLast1);
  return dirName;
}

module.exports = getLastDirName;

